% Reference: Wu, J., Sun, Y., Wang, M., Liu, M. (2019) 
%               Hand-eye Calibration: 4D Procrustes Analysis Approach,
%               Submitted to IEEE Trans. Instrum. Meas.


function [R, t, X] = proposed(A, B, dd)
    s = size(A);
    len = s(3);
    
    J = zeros(8, 4);
    for i = 1 : len
        SA = SE3_SO4(A(:, :, i), dd);
        SB = SE3_SO4(B(:, :, i), dd);
        Ji = Js(SA, SB);
        J = J + Ji / len;
    end
    
    g = [eig4(J(1 : 4, :)); eig4(J(5 : 8, :))];
    
    R = [g(1), -g(2), -g(3), -g(4);
         g(2), g(1), -g(4), g(3);
         g(3), g(4), g(1), -g(2);
         g(4), -g(3), g(2), g(1)] * [g(5), -g(6), -g(7), -g(8);
                                     g(6), g(5), g(8), -g(7);
                                     g(7), -g(8), g(5), g(6);
                                     g(8), g(7), -g(6), g(5)];
    t = - R(1 : 3, 4) * dd;
    R = - R(1 : 3, 1 : 3);
    X = [R, t;
         zeros(1, 3), 1];
end

